INT Risob(int x, int y, int k, int b)  // Рисование объекта на синем поле
{
    LOGFONT font1;
    font1.lfHeight = 10;// Устанавливает высоту шрифта или символа
    font1.lfWidth = 6;// Устанавливает среднюю ширину символов в шрифте
    font1.lfEscapement = 0;// Устанавливает угол, между вектором наклона и осью X устройства
    font1.lfOrientation = 0;// Устанавливает угол, между основной линией каждого символа и осью X устройства
    font1.lfWeight = 900;// Устанавливает толщину шрифта в диапазоне от 0 до 1000
    font1.lfItalic = 0;// Устанавливает курсивный шрифт
    font1.lfUnderline = 0;// Устанавливает подчеркнутый шрифт
    font1.lfStrikeOut = 0;// Устанавливает зачеркнутый шрифт
    font1.lfCharSet = RUSSIAN_CHARSET;// Устанавливает набор символов
    font1.lfOutPrecision = 0;// Устанавливает точность вывода
    font1.lfClipPrecision = 0;// Устанавливает точность отсечения
    font1.lfQuality = 0;// Устанавливает качество вывода
    font1.lfPitchAndFamily = 0;// Устанавливает ширину символов и семейство шрифта 

    HFONT hfont1 = CreateFontIndirect(&font1);

    TCHAR s[] = _T("АБВГДЕЖЗИК ");    // Набор используемых символов
    TCHAR n[] = _T(" ");           // Рабочий массивчик
    n[0] = s[b];                   // Выбираем заданный символ
    if (k == 0) { SetBkColor(hdc, RGB(255, 0, 0)); SetTextColor(hdc, RGB(255, 255, 255)); }
    else { SetBkColor(hdc, RGB(255, 255, 255)); SetTextColor(hdc, RGB(0, 0, 0)); }
    SelectObject(hdc, hfont1);
    TextOut(hdc, x * 11 + 10, y * 11 + 6, n, 1);
    DeleteObject(hfont1);
    return 0;
}

INT Marsh(int x, int y, int nl)   // Формирование маршрута - рекурсивная функция. Движимся в обратную сторону - к начальной точке 
{
    int tk, xx, yy, l, n, dx, dy;
    bool b;
    if (nl == 1) { marshrutx[1] = x;  marshruty[1] = y; return 1; }  // Спустились на первую линию. Останавливаем процесс поиска 
    tk = nach[nl] - 1;    // Номер последнего элемента предыдущей линии
    l = nl - 1;           
    n = nach[l];          // Номер первого элемента предыдущей линии
    while (n <= tk) {  // Просматриваем элементы предыдущей линии
                      xx = marshrutx[n]; yy = marshruty[n]; dx = xx - x; dy = yy - y;          
                      if (abs(dx) < 2) if (abs(dy) < 2)          // Элемент находится в шаговой доступности
                                                        { if ((dx == 0) || (dy == 0)) b = true; // Движение по горизонтали или по вертикали
                                                          else if ((w[xx][y] & 0x1) == 0x0) b = true;  // Дополнительная проверка для хода по диагонали
                                                               else b = ((w[x][yy] & 0x1) == 0x0);   // Дополнительная проверка для хода по диагонали
                                                          if (b)     // Новая точка подходит по всем параметрам
                                                          if (Marsh(xx, yy, l))  // Спускаемся на предыдущую линию. Если получаем сигнал окончания поиска,
                                                                                 { marshrutx[nl] = x;  marshruty[nl] = y; return 1; }                 //  то передаем его наверх. Перед этим запоминаем коодинаты точки
                                                         }
                      n++;
                    }
    return 0;
}

