int Volna(int x, int y)  // Волна
{   int xx, yy, tk, x1, y1;
    bool b = true;
    marshrutx[0] = x; marshruty[0] = y;    // Координаты старта
    nach[0] = 0;                       // Начало нулевой линии волны
    tk = -1;           // Конец предыдущей линии волны
    nmarshrut = 0;   // Номер последнего занятого элемента в массивах координат
    nlin = 0; // Номер линии волны
    w[x][y] |= 0x8;  // Устанавливаем единицу в бит 3 - признак того, что эту точку достигла волна
    while (b) { 
                if (tk == nmarshrut) { nlin = 0; break; } // Новых точек не найдено. Достигнуть финиша невозможно
                nlin++; nach[nlin] = nmarshrut + 1;     // Начало следующей линии волны
                tn = nach[nlin - 1]; tk = nmarshrut;     // Начальные и конечные номера координат текущей линии волны
                while (tn <= tk) { x1 = marshrutx[tn]; y1 = marshruty[tn]; 
                                   tn++;
                                   for (int i = 0; i < 8; i++) { // Проверяем 8 точек, расположенных вокруг в шаговой доступности
                                                                 xx = x1 + ex[i]; 
                                                                 if (xx > -1) if (xx < razx) { // Координата X попадает в синее поле
                                                                                               yy = y1 + ey[i];
                                                                                               if (yy > -1) if (yy < razy) { // Координата Y попадает в синее поле
                                                                                                                             if ((w[xx][yy] & 0x9) == 0x0) // Это море и волны здесь пока не было
                                                                                                                             if (i > 3) { // Дополнительная проверка для хода по диагонали
                                                                                                                                          if (((w[x1 + ex1[i]][y1 + ey1[i]] & 0x1) == 0x0) ||
                                                                                                                                             ((w[x1 + ex2[i]][y1 + ey2[i]] & 0x1) == 0x0)) // Сюда ходить можно
                                                                                                                                             { if ((xx == finx) && (yy == finy)) { // Найдена точка финиша
                                                                                                                                                                                   tk = -1;  // Для выхода из цикла
                                                                                                                                                                                   b = false;  // Для выхода из цикла
                                                                                                                                                                                   break;
                                                                                                                                                                                 }
                                                                                                                                               nmarshrut++; marshrutx[nmarshrut] = xx; // Записываем новую точку
                                                                                                                                               marshruty[nmarshrut] = yy;            // Записываем новую точку
                                                                                                                                               w[xx][yy] |= 0x8; // Устанавливаем единицу в бит 3 - признак того, что эту точку достигла волна
                                                                                                                                             }
                                                                                                                                          }
                                                                                                                             else { if ((xx == finx) && (yy == finy)) {  // Найдена точка финиша
                                                                                                                                                                         tk = -1;    // Для выхода из цикла
                                                                                                                                                                         b = false;   // Для выхода из цикла
                                                                                                                                                                         break;
                                                                                                                                                                      }
                                                                                                                                     nmarshrut++; marshrutx[nmarshrut] = xx; marshruty[nmarshrut] = yy; // Записываем новую точку
                                                                                                                                     w[xx][yy] |= 0x8;  // Устанавливаем единицу в бит 3 - признак того, что эту точку достигла волна
                                                                                                                                   }
                                                                                                                            }
                                                                                             }
                                                               }
                                 }
              }
    for (int i = 0; i < nmarshrut + 1; i++) w[marshrutx[i]][marshruty[i]] &= 0x7;  // Обнуляем бит 3 - удаляем следы волны
    if (nlin) { Marsh(x1, y1, nlin - 1); }  // Если удалось найти финишную точу, то строим маршрут. Длина маршрута уже известна - nlin
    return 0;
}

INT Planet()   // Создание моря и суши
{   srand(static_cast<unsigned int>(time(0)));
    for (int i = 0; i < razx; i++) for (int j = 0; j < razy; j++)  if ((rand() % 4) == 0)  w[i][j] = 0x1;  else  w[i][j] = 0x0;
    return 0;
}

INT Rissusha(){// Суша - зеленые круги
hPen = CreatePen(PS_SOLID, 1, RGB(0, 255, 55));  hBrush = CreateSolidBrush(RGB(0, 255, 55));
for (int j = 0; j < razy; j++) { for (int i = 0; i < razx; i++) { if (w[i][j] == 0x1) {  // Суша
                                                                                        SelectObject(hdc, hPen); Ellipse(hdc, i * 11 + 5, j * 11 + 2, i * 11 + 23, j * 11 + 20);
                                                                                        SelectObject(hdc, hBrush); ExtFloodFill(hdc, i * 11 + 14, j * 11 + 11, RGB(0, 255, 55), FLOODFILLBORDER);
                                                                                      }
                                                                }
                               }
DeleteObject(hBrush);
DeleteObject(hPen);  
return 0;
}

INT Vibvar() {    //  Выбор варианта продолжения диалога
    int tix, tiy;
    if (curx < 790)if (curx > 8) if (cury > 5) if (cury < 1007) {  // Курсор на синем поле
                                                                  tix = (curx - 9) / 11; tiy = (cury - 6) / 11;  // Координаты выбранной точки
                                                                  if ((w[tix][tiy] & 0x1) == 0x0) // Море
                                                                                                   if (var == 0) { sinx = tix; siny = tiy; var = 1; } // Выбрана начальная точка
                                                                                                   else if (var == 1) { finx = tix; finy = tiy;    // Выбрана конечная точка
                                                                                                               Volna(sinx, siny); var = 2;       // Запускаем волну
                                                                                                             }
                                                                                                        else var = 0; // Начинаем диалог сначала
                                                                }
    return 0;
}